    private boolean verify(){
        if(txtRGM.getText().equalsIgnoreCase(null)|| txtRGM.getText().equalsIgnoreCase("") ||
           txtNomeAluno.getText().equalsIgnoreCase(null) || txtNomeAluno.getText().equalsIgnoreCase("")||
           Integer.parseInt(txtIdadeAluno.getText()) <= 0 ||
           txtCursoAluno.getText() ==  null || txtCursoAluno.getText().equalsIgnoreCase("")){
            return true;
        }else{
            return false;
        }
    }
    
    private boolean verifyRGM(){
        if(txtRGM.getText().equalsIgnoreCase("") || txtRGM.getText().equalsIgnoreCase(null)){
            return true;
        }
        else{
            return false;
        }
    }
    
    private boolean verifyCourse(){
        if(txtCursoAluno.getText().equalsIgnoreCase(null) || txtCursoAluno.getText().equalsIgnoreCase("")){
            return true;
        }
        else{
            return false;
        }
    }
    
