    private void btnStoreActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnStoreActionPerformed
        if(this.verify()){
            JOptionPane.showMessageDialog(null, "Please, to continue with this action, complete all the fields first");
        }else{
            alunoController ac = new alunoController();
            alunos student = new alunos(txtRGM.getText(), txtNomeAluno.getText(),Integer.parseInt(txtIdadeAluno.getText()), txtCursoAluno.getText());
            try {
                
                ac.store(student);
                JOptionPane.showMessageDialog(null, "Student inserted with success! ");
            } catch (ClassNotFoundException ex) {
                throw new RuntimeException("Error in create a new Student" + ex);
            }
        }
    }//GEN-LAST:event_btnStoreActionPerformed

    private void btnIndexActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIndexActionPerformed
        if(this.verifyCourse()){
            JOptionPane.showMessageDialog(null, "Please, to continue with this action, complete the field of course ");
        }
        else{
            txtaListaAluno.setText("");
            alunos student = new alunos();
            student.setCurso(txtCursoAluno.getText());
            student.setIdade(0);
            student.setNome("");
            student.setRGM("");
            alunoController ac = new alunoController();
            
            try {
                ArrayList<alunos> allStudents = ac.index(student);
                
                for(int i = 0; i < allStudents.size(); i++){
                    txtaListaAluno.setText(txtaListaAluno.getText() + allStudents.get(i).getRGM() + " | " 
                            + allStudents.get(i).getNome() + " | " + allStudents.get(i).getIdade() 
                            + " | " + allStudents.get(i).getCurso() + "\n");
                }
                
            } catch (ClassNotFoundException ex) {
                throw new RuntimeException("Error to list the students " + ex);
            }
        }
    }//GEN-LAST:event_btnIndexActionPerformed

    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        if(this.verify()){
            JOptionPane.showMessageDialog(null, "To continue with this action, please complete all the fields ");
        }else{
            alunos student = new alunos(txtRGM.getText(), txtNomeAluno.getText(),Integer.parseInt(txtIdadeAluno.getText()), txtCursoAluno.getText());
            alunoController ac = new alunoController();
            
            try {
                ac.update(student);
                JOptionPane.showMessageDialog(null, "Student updated with success ");
            } catch (ClassNotFoundException ex) {
                throw new RuntimeException("Error in update a student" + ex);
            }
            
        }
    }//GEN-LAST:event_btnUpdateActionPerformed

    private void btnDestroyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDestroyActionPerformed
        if(this.verifyRGM()){
            JOptionPane.showMessageDialog(null, "To continue, please insert the field of RGM ");
        }else{
            alunos student = new alunos();
            student.setRGM(txtRGM.getText());
            student.setCurso("");
            student.setNome("");
            student.setIdade(0);
            
            alunoController ac = new alunoController();
            try {
                ac.destroy(student);
                JOptionPane.showMessageDialog(null, "Student deleted with success! ");
            } catch (ClassNotFoundException ex) {
                throw new RuntimeException("Error to delete a student "+ ex);
            }
        }
    }//GEN-LAST:event_btnDestroyActionPerformed

