    private boolean verify(){
        if(txtRGM.getText() == null || txtRGM.getText() == "" ||
            txtNome.getText() == null || txtNome.getText() == "" ||
            txtCidade.getText() == null || txtCidade.getText() == "" ||
            txtEndereco.getText() == null || txtEndereco.getText() == "" ||
            txtEstado.getText() == null || txtEstado.getText() == ""){
            return false;
        }else{
            return true;
        }
    }
    
    private void btnInserirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInserirActionPerformed
        if(!this.verify()){
            JOptionPane.showMessageDialog(null, "Para continuar, complete todos os campos ");
        }else{
            AlunoModel am = new AlunoModel(txtRGM.getText(), txtNome.getText(), txtEndereco.getText(), txtCidade.getText(), txtEstado.getText());
            ac.store(am);
        }
    }//GEN-LAST:event_btnInserirActionPerformed

    private void btnDeletarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeletarActionPerformed
        if(txtRGM.getText() == null || txtRGM.getText() == ""){
            JOptionPane.showMessageDialog(null, "Insira pelo menos o rgm para deletar o aluno");
        }
        else{
            AlunoModel am = new AlunoModel(txtRGM.getText(), txtNome.getText(), txtEndereco.getText(), txtCidade.getText(), txtEstado.getText());
            ac.destroy(am);
        }
    }//GEN-LAST:event_btnDeletarActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
        if(!this.verify()){
            JOptionPane.showMessageDialog(null, "Para continuar, complete todos os campos ");
        }else{
            AlunoModel am = new AlunoModel(txtRGM.getText(), txtNome.getText(), txtEndereco.getText(), txtCidade.getText(), txtEstado.getText());
            ac.update(am);
        }
    }//GEN-LAST:event_btnAtualizarActionPerformed

